<template>
    <div>
        <h2>Register</h2>
        <form @submit.prevent="handleSubmit">
            <div class="form-group">
                <label for="email">Email</label>
                <input type="email" v-model="user.email" v-validate="{ required: true, email: true }" id="email" name="email" class="form-control" :class="{ 'is-invalid': submitted && errors.has('email') }" />
                <div v-if="submitted && errors.has('email')" class="invalid-feedback">{{ errors.first('email') }}</div>
            </div>
            <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" v-model="user.password" v-validate="{ required: true, min: 6 }" name="password" class="form-control" :class="{ 'is-invalid': submitted && errors.has('password') }" />
                <div v-if="submitted && errors.has('password')" class="invalid-feedback">{{ errors.first('password') }}</div>
            </div>
            <div class="form-group">
                <button class="btn btn-primary" :disabled="status.registering">Register</button>
                <img v-show="status.registering" src="data:image/gif;base64," />
                <router-link to="/login" class="btn btn-link">Cancel</router-link>
            </div>
        </form>
    </div>
</template>

<script>
	import { mapState, mapActions } from 'vuex'

	export default {
		data () {
			return {
				user: {
					email: '',
					password: ''
				},
				submitted: false
			}
		},
		computed: {
			...mapState('account', ['status'])
		},
		methods: {
			...mapActions('account', ['register']),
			handleSubmit(e) {
				this.submitted = true;
				this.$validator.validate().then(valid => {
					if (valid) {
						this.register(this.user);
					}
				});
			}
		}
	};
</script>

